import React, { useState } from 'react';
import { Reply, MoreHorizontal, Trash2 } from 'lucide-react';
import { Comment } from '../../types/comment';
import { commentsApi } from '../../api/comments';
import { useQueryClient } from 'react-query';
import { useAuthStore } from '../../stores/authStore';
import toast from 'react-hot-toast';

interface CommentItemProps {
  comment: Comment;
  onReply: (comment: Comment) => void;
  onDelete: (commentId: number) => void;
}

const CommentItem: React.FC<CommentItemProps> = ({ comment, onReply, onDelete }) => {
  const queryClient = useQueryClient();
  const { user } = useAuthStore();
  const [showReplies, setShowReplies] = useState(false);
  const [showMoreOptions, setShowMoreOptions] = useState(false);

  const formatTime = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const minutes = Math.floor(diff / (1000 * 60));
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const days = Math.floor(hours / 24);

    if (minutes < 1) return '刚刚';
    if (minutes < 60) return `${minutes}分钟前`;
    if (hours < 24) return `${hours}小时前`;
    return `${days}天前`;
  };


  const handleDelete = () => {
    if (window.confirm('确定要删除这条评论吗？')) {
      onDelete(comment.id);
    }
  };

  const isOwnComment = user && user.id === comment.author.id;
  const hasReplies = comment.replies && comment.replies.length > 0;

  return (
    <div className="border-b border-gray-100 last:border-b-0">
      <div className="flex space-x-3 py-4">
        <img
          src={comment.author.avatar_url || '/default-avatar.svg'}
          alt={comment.author.nickname}
          className="w-8 h-8 rounded-full flex-shrink-0"
        />
        <div className="flex-1 min-w-0">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <span className="font-medium text-gray-900 text-sm">
                {comment.author.nickname}
              </span>
              <span className="text-gray-500 text-xs">
                {formatTime(comment.created_at)}
              </span>
            </div>
            {isOwnComment && (
              <div className="relative">
                <button
                  onClick={() => setShowMoreOptions(!showMoreOptions)}
                  className="p-1 hover:bg-gray-100 rounded-full"
                >
                  <MoreHorizontal className="w-4 h-4 text-gray-500" />
                </button>
                {showMoreOptions && (
                  <div className="absolute right-0 mt-1 w-32 bg-white rounded-md shadow-lg border border-gray-200 z-10">
                    <button
                      onClick={handleDelete}
                      className="w-full px-3 py-2 text-left text-red-600 hover:bg-red-50 flex items-center text-sm"
                    >
                      <Trash2 className="w-3 h-3 mr-2" />
                      删除
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>
          
          <p className="text-gray-900 text-sm mt-1 whitespace-pre-wrap">
            {comment.content}
          </p>
          
          <div className="flex items-center space-x-4 mt-2">
            <button
              onClick={handleLike}
              disabled={isLiking}
              className={`flex items-center space-x-1 text-xs transition-colors ${
                comment.is_liked ? 'text-red-500' : 'text-gray-500 hover:text-red-500'
              } ${isLiking ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              <Heart className={`w-3 h-3 ${comment.is_liked ? 'fill-current' : ''}`} />
              <span>{comment.likes_count || 0}</span>
            </button>
            
            <button
              onClick={() => onReply(comment)}
              className="flex items-center space-x-1 text-xs text-gray-500 hover:text-gray-700 transition-colors"
            >
              <Reply className="w-3 h-3" />
              <span>回复</span>
            </button>
          </div>
          
          {/* 回复列表 */}
          {hasReplies && (
            <div className="mt-3">
              <button
                onClick={() => setShowReplies(!showReplies)}
                className="text-xs text-gray-500 hover:text-gray-700 mb-2"
              >
                {showReplies ? '隐藏' : '查看'} {comment.replies.length} 条回复
              </button>
              
              {showReplies && (
                <div className="space-y-3 pl-4 border-l-2 border-gray-100">
                  {comment.replies.map((reply) => (
                    <CommentItem
                      key={reply.id}
                      comment={reply}
                      onReply={onReply}
                      onDelete={onDelete}
                    />
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CommentItem;
