import React, { useState, useEffect, useRef } from 'react';
import { Send, MoreVertical, Phone, Video, MessageCircle } from 'lucide-react';
import { useAuthStore } from '../stores/authStore';

interface Message {
  id: string;
  content: string;
  senderId: string;
  receiverId: string;
  timestamp: Date;
  isRead: boolean;
}

interface ChatUser {
  id: string;
  username: string;
  avatar: string;
  isOnline: boolean;
  lastSeen?: Date;
}

const Chat: React.FC = () => {
  const { user } = useAuthStore();
  const [selectedChat, setSelectedChat] = useState<string | null>(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [chatUsers, setChatUsers] = useState<ChatUser[]>([]);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // 模拟聊天用户数据
  useEffect(() => {
    setChatUsers([
      {
        id: '1',
        username: '小樱',
        avatar: '/api/placeholder/40/40',
        isOnline: true,
      },
      {
        id: '2',
        username: '鸣人',
        avatar: '/api/placeholder/40/40',
        isOnline: false,
        lastSeen: new Date(Date.now() - 30 * 60 * 1000), // 30分钟前
      },
      {
        id: '3',
        username: '路飞',
        avatar: '/api/placeholder/40/40',
        isOnline: true,
      },
    ]);
  }, []);

  // 模拟消息数据
  useEffect(() => {
    if (selectedChat) {
      setMessages([
        {
          id: '1',
          content: '你好！最近怎么样？',
          senderId: selectedChat,
          receiverId: user?.id?.toString() || '',
          timestamp: new Date(Date.now() - 60 * 60 * 1000),
          isRead: true,
        },
        {
          id: '2',
          content: '还不错，你呢？最近在看什么动漫？',
          senderId: user?.id?.toString() || '',
          receiverId: selectedChat,
          timestamp: new Date(Date.now() - 30 * 60 * 1000),
          isRead: true,
        },
        {
          id: '3',
          content: '在追《鬼灭之刃》，真的太好看了！',
          senderId: selectedChat,
          receiverId: user?.id?.toString() || '',
          timestamp: new Date(Date.now() - 10 * 60 * 1000),
          isRead: true,
        },
      ]);
    }
  }, [selectedChat, user]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (!message.trim() || !selectedChat) return;

    const newMessage: Message = {
      id: Date.now().toString(),
      content: message,
      senderId: user?.id?.toString() || '',
      receiverId: selectedChat,
      timestamp: new Date(),
      isRead: false,
    };

    setMessages(prev => [...prev, newMessage]);
    setMessage('');
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('zh-CN', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const selectedUser = chatUsers.find(u => u.id === selectedChat);

  return (
    <div className="flex h-[calc(100vh-4rem)] bg-white">
      {/* 聊天列表 */}
      <div className="w-80 border-r border-gray-200 flex flex-col">
        <div className="p-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-900">私信</h2>
        </div>

        <div className="flex-1 overflow-y-auto">
          {chatUsers.map((chatUser) => (
            <div
              key={chatUser.id}
              onClick={() => setSelectedChat(chatUser.id)}
              className={`p-4 border-b border-gray-100 cursor-pointer hover:bg-gray-50 transition-colors ${
                selectedChat === chatUser.id ? 'bg-primary-50 border-primary-200' : ''
              }`}
            >
              <div className="flex items-center space-x-3">
                <div className="relative">
                  <img
                    src={chatUser.avatar}
                    alt={chatUser.username}
                    className="w-10 h-10 rounded-full object-cover"
                  />
                  {chatUser.isOnline && (
                    <div className="absolute bottom-0 right-0 w-3 h-3 bg-green-500 rounded-full border-2 border-white"></div>
                  )}
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-gray-900 truncate">
                    {chatUser.username}
                  </p>
                  <p className="text-xs text-gray-500">
                    {chatUser.isOnline ? '在线' : `${formatTime(chatUser.lastSeen || new Date())} 最后在线`}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* 聊天区域 */}
      {selectedChat ? (
        <div className="flex-1 flex flex-col">
          {/* 聊天头部 */}
          <div className="p-4 border-b border-gray-200 flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <img
                src={selectedUser?.avatar}
                alt={selectedUser?.username}
                className="w-8 h-8 rounded-full object-cover"
              />
              <div>
                <h3 className="text-sm font-medium text-gray-900">
                  {selectedUser?.username}
                </h3>
                <p className="text-xs text-gray-500">
                  {selectedUser?.isOnline ? '在线' : '离线'}
                </p>
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-full hover:bg-gray-100">
                <Phone className="w-5 h-5" />
              </button>
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-full hover:bg-gray-100">
                <Video className="w-5 h-5" />
              </button>
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-full hover:bg-gray-100">
                <MoreVertical className="w-5 h-5" />
              </button>
            </div>
          </div>

          {/* 消息列表 */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {messages.map((msg) => (
              <div
                key={msg.id}
                className={`flex ${msg.senderId === user?.id?.toString() ? 'justify-end' : 'justify-start'}`}
              >
                <div
                  className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                    msg.senderId === user?.id?.toString()
                      ? 'bg-primary-600 text-white'
                      : 'bg-gray-100 text-gray-900'
                  }`}
                >
                  <p className="text-sm">{msg.content}</p>
                  <p
                    className={`text-xs mt-1 ${
                      msg.senderId === user?.id?.toString() ? 'text-primary-100' : 'text-gray-500'
                    }`}
                  >
                    {formatTime(msg.timestamp)}
                  </p>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          {/* 消息输入 */}
          <div className="p-4 border-t border-gray-200">
            <div className="flex items-end space-x-2">
              <div className="flex-1">
                <textarea
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="输入消息..."
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  rows={1}
                  style={{ minHeight: '40px', maxHeight: '120px' }}
                />
              </div>
              <button
                onClick={handleSendMessage}
                disabled={!message.trim()}
                className="p-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                <Send className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      ) : (
        <div className="flex-1 flex items-center justify-center bg-gray-50">
          <div className="text-center">
            <MessageCircle className="w-16 h-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">选择一个聊天</h3>
            <p className="text-gray-500">从左侧选择一个用户开始聊天</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chat;
