import apiClient from './client';
import { Post, CreatePostRequest, PostFilters } from '../types/post';

export const postsApi = {
  // 获取帖子列表
  getPosts: async (filters: PostFilters = {}): Promise<{ results: Post[]; count: number; next: string | null }> => {
    const params = new URLSearchParams();
    
    if (filters.post_type) params.append('post_type', filters.post_type);
    if (filters.circle_id) params.append('circle_id', filters.circle_id.toString());
    if (filters.author_id) params.append('author_id', filters.author_id.toString());
    if (filters.tags?.length) params.append('tags', filters.tags.join(','));
    if (filters.search) params.append('search', filters.search);
    if (filters.ordering) params.append('ordering', filters.ordering);
    
    const response = await apiClient.get(`/posts/?${params.toString()}`);
    return response.data;
  },

  // 获取帖子详情
  getPost: async (postId: number): Promise<Post> => {
    const response = await apiClient.get(`/posts/${postId}/`);
    return response.data;
  },

  // 创建帖子
  createPost: async (data: CreatePostRequest): Promise<Post> => {
    const formData = new FormData();
    formData.append('title', data.title);
    formData.append('content', data.content);
    formData.append('post_type', data.post_type);
    formData.append('tags', JSON.stringify(data.tags));
    
    if (data.circle_id) {
      formData.append('circle_id', data.circle_id.toString());
    }
    
    data.images.forEach((image, index) => {
      formData.append(`images`, image);
    });
    
    const response = await apiClient.post('/posts/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  },

  // 更新帖子
  updatePost: async (postId: number, data: Partial<CreatePostRequest>): Promise<Post> => {
    const response = await apiClient.patch(`/posts/${postId}/`, data);
    return response.data;
  },

  // 删除帖子
  deletePost: async (postId: number): Promise<void> => {
    await apiClient.delete(`/posts/${postId}/`);
  },

  // 点赞帖子
  likePost: async (postId: number): Promise<void> => {
    await apiClient.post(`/posts/${postId}/like/`);
  },

  // 取消点赞帖子
  unlikePost: async (postId: number): Promise<void> => {
    await apiClient.delete(`/posts/${postId}/like/`);
  },

  // 收藏帖子
  bookmarkPost: async (postId: number): Promise<void> => {
    await apiClient.post(`/posts/${postId}/bookmark/`);
  },

  // 取消收藏帖子
  unbookmarkPost: async (postId: number): Promise<void> => {
    await apiClient.delete(`/posts/${postId}/bookmark/`);
  },

  // 分享帖子
  sharePost: async (postId: number, platform: string): Promise<void> => {
    await apiClient.post(`/posts/${postId}/share/`, { platform });
  },

  // 举报帖子
  reportPost: async (postId: number, reportType: string, reason: string): Promise<void> => {
    await apiClient.post(`/posts/${postId}/report/`, {
      report_type: reportType,
      reason,
    });
  },

  // 获取热门帖子
  getTrendingPosts: async (): Promise<Post[]> => {
    const response = await apiClient.get('/posts/trending/');
    return response.data;
  },

  // 获取推荐帖子
  getRecommendedPosts: async (): Promise<Post[]> => {
    const response = await apiClient.get('/posts/recommended/');
    return response.data;
  },
};
