import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery } from 'react-query';
import { ArrowLeft, Heart, MessageCircle, Share2, Bookmark, Eye } from 'lucide-react';
import { postsApi } from '../api/posts';
import { Post } from '../types/post';

const PostDetail: React.FC = () => {
  const { postId } = useParams<{ postId: string }>();
  const [selectedImageIndex, setSelectedImageIndex] = useState<number | null>(null);

  const { data: post, isLoading, error } = useQuery<Post>(
    ['post', postId],
    () => postsApi.getPost(Number(postId!)),
    {
      enabled: !!postId,
    }
  );

  if (isLoading) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <div className="animate-pulse">
            <div className="h-8 bg-gray-200 rounded w-1/3 mb-4"></div>
            <div className="h-4 bg-gray-200 rounded w-1/2 mb-2"></div>
            <div className="h-4 bg-gray-200 rounded w-2/3"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !post) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h1 className="text-2xl font-bold text-gray-900">帖子不存在</h1>
          <p className="text-gray-600 mt-2">该帖子可能已被删除或不存在。</p>
          <Link
            to="/"
            className="inline-flex items-center mt-4 text-primary-600 hover:text-primary-700"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            返回首页
          </Link>
        </div>
      </div>
    );
  }

  const handleImageClick = (index: number) => {
    setSelectedImageIndex(index);
  };

  const closeImageModal = () => {
    setSelectedImageIndex(null);
  };

  const nextImage = () => {
    if (selectedImageIndex !== null && post.images) {
      setSelectedImageIndex((selectedImageIndex + 1) % post.images.length);
    }
  };

  const prevImage = () => {
    if (selectedImageIndex !== null && post.images) {
      setSelectedImageIndex(
        selectedImageIndex === 0 ? post.images.length - 1 : selectedImageIndex - 1
      );
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      {/* 返回按钮 */}
      <div className="mb-6">
        <Link
          to="/"
          className="inline-flex items-center text-gray-600 hover:text-gray-900 transition-colors"
        >
          <ArrowLeft className="w-5 h-5 mr-2" />
          返回首页
        </Link>
      </div>

      {/* 帖子内容 */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
        {/* 作者信息 */}
        <div className="p-6 border-b border-gray-100">
          <div className="flex items-center space-x-3">
            <img
              src={post.author.avatar_url || '/default-avatar.svg'}
              alt={post.author.nickname}
              className="w-12 h-12 rounded-full"
            />
            <div>
              <h3 className="font-medium text-gray-900">{post.author.nickname}</h3>
              <p className="text-sm text-gray-500">
                {new Date(post.created_at).toLocaleString('zh-CN')}
              </p>
            </div>
          </div>
        </div>

        {/* 帖子标题和内容 */}
        <div className="p-6">
          <h1 className="text-2xl font-bold text-gray-900 mb-4">{post.title}</h1>
          <div className="prose max-w-none">
            <p className="text-gray-700 leading-relaxed whitespace-pre-wrap">
              {post.content}
            </p>
          </div>
        </div>

        {/* 图片展示 */}
        {post.images && post.images.length > 0 && (
          <div className="px-6 pb-6">
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
              {post.images.map((imageUrl, index) => (
                <div
                  key={index}
                  className="relative group cursor-pointer"
                  onClick={() => handleImageClick(index)}
                >
                  <img
                    src={imageUrl}
                    alt={`图片 ${index + 1}`}
                    className="w-full h-48 object-cover rounded-lg hover:opacity-90 transition-opacity"
                  />
                  <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-10 transition-all duration-200 rounded-lg flex items-center justify-center">
                    <Eye className="w-8 h-8 text-white opacity-0 group-hover:opacity-100 transition-opacity" />
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* 互动按钮 */}
        <div className="px-6 py-4 border-t border-gray-100">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-6">
              <button className="flex items-center space-x-2 text-gray-500 hover:text-red-500 transition-colors">
                <Heart className="w-5 h-5" />
                <span>{post.likes_count}</span>
              </button>
              <button className="flex items-center space-x-2 text-gray-500 hover:text-blue-500 transition-colors">
                <MessageCircle className="w-5 h-5" />
                <span>{post.comments_count || 0}</span>
              </button>
              <button className="flex items-center space-x-2 text-gray-500 hover:text-green-500 transition-colors">
                <Share2 className="w-5 h-5" />
                <span>{post.shares_count || 0}</span>
              </button>
            </div>
            <button className="flex items-center space-x-2 text-gray-500 hover:text-yellow-500 transition-colors">
              <Bookmark className="w-5 h-5" />
              <span>{post.bookmarks_count || 0}</span>
            </button>
          </div>
        </div>
      </div>

      {/* 图片查看模态框 */}
      {selectedImageIndex !== null && post.images && (
        <div className="fixed inset-0 bg-black bg-opacity-90 z-50 flex items-center justify-center">
          <div className="relative max-w-4xl max-h-full p-4">
            <button
              onClick={closeImageModal}
              className="absolute top-4 right-4 text-white hover:text-gray-300 z-10"
            >
              <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
            
            <img
              src={post.images[selectedImageIndex]}
              alt={`图片 ${selectedImageIndex + 1}`}
              className="max-w-full max-h-full object-contain"
            />
            
            {post.images.length > 1 && (
              <>
                <button
                  onClick={prevImage}
                  className="absolute left-4 top-1/2 transform -translate-y-1/2 text-white hover:text-gray-300"
                >
                  <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                  </svg>
                </button>
                <button
                  onClick={nextImage}
                  className="absolute right-4 top-1/2 transform -translate-y-1/2 text-white hover:text-gray-300"
                >
                  <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                  </svg>
                </button>
              </>
            )}
            
            <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white text-sm">
              {selectedImageIndex + 1} / {post.images.length}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PostDetail;
