import React, { useState, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Camera, Edit3, Save, X, Heart, MessageSquare, Users, FileText, Settings } from 'lucide-react';
import { useAuthStore } from '../stores/authStore';
import { useForm } from 'react-hook-form';
import toast from 'react-hot-toast';

interface ProfileForm {
  nickname: string;
  signature: string;
  bio: string;
}

const Profile: React.FC = () => {
  const { userId } = useParams<{ userId: string }>();
  const { user } = useAuthStore();
  const fileInputRef = useRef<HTMLInputElement>(null);
  
  const [isEditing, setIsEditing] = useState(false);
  const [activeTab, setActiveTab] = useState<'posts' | 'likes' | 'following' | 'followers'>('posts');
  const [avatarPreview, setAvatarPreview] = useState<string | null>(null);
  const [isUploading, setIsUploading] = useState(false);

  // 模拟用户数据
  const profileUser = {
    id: parseInt(userId || '1'),
    nickname: user?.nickname || '用户昵称',
    email: user?.email || 'user@example.com',
    avatar_url: user?.avatar_url || '/static/images/default-avatar.png',
    signature: '这个人很懒，什么都没有留下...',
    bio: '热爱二次元文化，喜欢动漫、游戏和轻小说。',
    followers_count: 128,
    following_count: 256,
    posts_count: 42,
    likes_received: 1024,
    is_following: false,
    created_at: '2024-01-01',
  };

  const isOwnProfile = !userId || parseInt(userId) === user?.id;

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm<ProfileForm>({
    defaultValues: {
      nickname: profileUser.nickname,
      signature: profileUser.signature,
      bio: profileUser.bio,
    },
  });

  const handleAvatarChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        toast.error('头像文件不能超过5MB');
        return;
      }

      const reader = new FileReader();
      reader.onload = (e) => {
        setAvatarPreview(e.target?.result as string);
      };
      reader.readAsDataURL(file);

      // 模拟上传
      setIsUploading(true);
      setTimeout(() => {
        setIsUploading(false);
        toast.success('头像上传成功！');
      }, 2000);
    }
  };

  const onSubmit = async (data: ProfileForm) => {
    try {
      // 模拟API调用
      console.log('更新资料:', data);
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      toast.success('资料更新成功！');
      setIsEditing(false);
    } catch (error) {
      toast.error('更新失败，请稍后重试');
    }
  };

  const handleFollow = () => {
    // 模拟关注/取消关注
    toast.success(profileUser.is_following ? '已取消关注' : '关注成功');
  };

  const mockPosts = [
    {
      id: 1,
      title: '今天看了《鬼灭之刃》最新一集',
      content: '炭治郎的成长真的让人感动...',
      likes_count: 42,
      comments_count: 15,
      created_at: '2024-01-15T10:30:00Z',
    },
    {
      id: 2,
      title: '分享一下我的手办收藏',
      content: '最近入手了几个新手办...',
      likes_count: 128,
      comments_count: 32,
      created_at: '2024-01-14T08:15:00Z',
    },
  ];

  const mockFollowing = [
    {
      id: 1,
      nickname: '动漫爱好者',
      avatar_url: '/static/images/default-avatar.png',
      signature: '每天都要看动漫！',
      followers_count: 1024,
    },
    {
      id: 2,
      nickname: '手办收藏家',
      avatar_url: '/static/images/default-avatar.png',
      signature: '手办就是我的生命',
      followers_count: 2048,
    },
  ];

  const renderTabContent = () => {
    switch (activeTab) {
      case 'posts':
        return (
          <div className="space-y-4">
            {mockPosts.map(post => (
              <div key={post.id} className="bg-white rounded-lg border border-gray-200 p-4">
                <Link to={`/posts/${post.id}`} className="block">
                  <h3 className="font-medium text-gray-900 hover:text-primary-600 mb-2">
                    {post.title}
                  </h3>
                  <p className="text-gray-600 text-sm mb-3 line-clamp-2">
                    {post.content}
                  </p>
                </Link>
                <div className="flex items-center justify-between text-sm text-gray-500">
                  <div className="flex items-center space-x-4">
                    <span className="flex items-center">
                      <Heart className="w-4 h-4 mr-1" />
                      {post.likes_count}
                    </span>
                    <span className="flex items-center">
                      <MessageSquare className="w-4 h-4 mr-1" />
                      {post.comments_count}
                    </span>
                  </div>
                  <span>{new Date(post.created_at).toLocaleDateString()}</span>
                </div>
              </div>
            ))}
          </div>
        );
      
      case 'likes':
        return (
          <div className="space-y-4">
            {mockPosts.map(post => (
              <div key={post.id} className="bg-white rounded-lg border border-gray-200 p-4">
                <Link to={`/posts/${post.id}`} className="block">
                  <h3 className="font-medium text-gray-900 hover:text-primary-600 mb-2">
                    {post.title}
                  </h3>
                  <p className="text-gray-600 text-sm mb-3 line-clamp-2">
                    {post.content}
                  </p>
                </Link>
                <div className="flex items-center justify-between text-sm text-gray-500">
                  <div className="flex items-center space-x-4">
                    <span className="flex items-center text-red-500">
                      <Heart className="w-4 h-4 mr-1 fill-current" />
                      已点赞
                    </span>
                  </div>
                  <span>{new Date(post.created_at).toLocaleDateString()}</span>
                </div>
              </div>
            ))}
          </div>
        );
      
      case 'following':
      case 'followers':
        return (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {mockFollowing.map(followUser => (
              <div key={followUser.id} className="bg-white rounded-lg border border-gray-200 p-4">
                <div className="flex items-center space-x-3">
                  <img
                    src={followUser.avatar_url}
                    alt={followUser.nickname}
                    className="w-12 h-12 rounded-full"
                  />
                  <div className="flex-1">
                    <Link
                      to={`/profile/${followUser.id}`}
                      className="font-medium text-gray-900 hover:text-primary-600"
                    >
                      {followUser.nickname}
                    </Link>
                    <p className="text-sm text-gray-600 mt-1">{followUser.signature}</p>
                    <p className="text-xs text-gray-500 mt-1">
                      {followUser.followers_count} 粉丝
                    </p>
                  </div>
                  <button className="px-3 py-1 text-sm bg-primary-600 text-white rounded-md hover:bg-primary-700">
                    {activeTab === 'following' ? '已关注' : '关注'}
                  </button>
                </div>
              </div>
            ))}
          </div>
        );
      
      default:
        return null;
    }
  };

  return (
    <div className="max-w-4xl mx-auto py-6">
      {/* 个人资料卡片 */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 mb-6">
        {/* 背景图 */}
        <div className="h-32 bg-gradient-to-r from-primary-400 to-primary-600 rounded-t-lg relative">
          {isOwnProfile && (
            <button className="absolute top-4 right-4 p-2 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70">
              <Camera className="w-4 h-4" />
            </button>
          )}
        </div>

        <div className="px-6 pb-6">
          {/* 头像和基本信息 */}
          <div className="flex flex-col sm:flex-row sm:items-end sm:space-x-6 -mt-16 relative">
            <div className="relative">
              <img
                src={avatarPreview || profileUser.avatar_url}
                alt={profileUser.nickname}
                className="w-32 h-32 rounded-full border-4 border-white shadow-lg"
              />
              {isOwnProfile && (
                <button
                  onClick={() => fileInputRef.current?.click()}
                  disabled={isUploading}
                  className="absolute bottom-2 right-2 p-2 bg-primary-600 text-white rounded-full hover:bg-primary-700 disabled:opacity-50"
                >
                  <Camera className="w-4 h-4" />
                </button>
              )}
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                onChange={handleAvatarChange}
                className="hidden"
              />
            </div>

            <div className="flex-1 mt-4 sm:mt-0">
              <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
                <div>
                  <h1 className="text-2xl font-bold text-gray-900">{profileUser.nickname}</h1>
                  <p className="text-gray-600 mt-1">{profileUser.signature}</p>
                  <p className="text-sm text-gray-500 mt-2">{profileUser.bio}</p>
                </div>
                
                <div className="flex items-center space-x-3 mt-4 sm:mt-0">
                  {isOwnProfile ? (
                    <button
                      onClick={() => setIsEditing(!isEditing)}
                      className="flex items-center px-4 py-2 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200"
                    >
                      {isEditing ? <X className="w-4 h-4 mr-2" /> : <Edit3 className="w-4 h-4 mr-2" />}
                      {isEditing ? '取消' : '编辑资料'}
                    </button>
                  ) : (
                    <button
                      onClick={handleFollow}
                      className={`px-4 py-2 rounded-md ${
                        profileUser.is_following
                          ? 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                          : 'bg-primary-600 text-white hover:bg-primary-700'
                      }`}
                    >
                      {profileUser.is_following ? '已关注' : '关注'}
                    </button>
                  )}
                  <Link
                    to={`/chat/${profileUser.id}`}
                    className="px-4 py-2 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200"
                  >
                    私信
                  </Link>
                </div>
              </div>

              {/* 统计数据 */}
              <div className="flex items-center space-x-6 mt-4">
                <div className="text-center">
                  <div className="text-xl font-bold text-gray-900">{profileUser.posts_count}</div>
                  <div className="text-sm text-gray-500">帖子</div>
                </div>
                <div className="text-center">
                  <div className="text-xl font-bold text-gray-900">{profileUser.followers_count}</div>
                  <div className="text-sm text-gray-500">粉丝</div>
                </div>
                <div className="text-center">
                  <div className="text-xl font-bold text-gray-900">{profileUser.following_count}</div>
                  <div className="text-sm text-gray-500">关注</div>
                </div>
                <div className="text-center">
                  <div className="text-xl font-bold text-gray-900">{profileUser.likes_received}</div>
                  <div className="text-sm text-gray-500">获赞</div>
                </div>
              </div>
            </div>
          </div>

          {/* 编辑表单 */}
          {isEditing && isOwnProfile && (
            <form onSubmit={handleSubmit(onSubmit)} className="mt-6 p-4 bg-gray-50 rounded-lg">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    昵称
                  </label>
                  <input
                    {...register('nickname', { required: '请输入昵称' })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                  />
                  {errors.nickname && (
                    <p className="mt-1 text-sm text-red-600">{errors.nickname.message}</p>
                  )}
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    个性签名
                  </label>
                  <input
                    {...register('signature')}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                    placeholder="写点什么介绍一下自己..."
                  />
                </div>
              </div>
              
              <div className="mt-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  个人简介
                </label>
                <textarea
                  {...register('bio')}
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                  placeholder="详细介绍一下自己..."
                />
              </div>
              
              <div className="flex items-center space-x-3 mt-4">
                <button
                  type="submit"
                  className="flex items-center px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700"
                >
                  <Save className="w-4 h-4 mr-2" />
                  保存
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setIsEditing(false);
                    reset();
                  }}
                  className="px-4 py-2 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200"
                >
                  取消
                </button>
              </div>
            </form>
          )}
        </div>
      </div>

      {/* 标签页 */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200">
        <div className="border-b border-gray-200">
          <nav className="flex space-x-8 px-6">
            <button
              onClick={() => setActiveTab('posts')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'posts'
                  ? 'border-primary-500 text-primary-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <FileText className="w-4 h-4 inline mr-2" />
              我的帖子 ({profileUser.posts_count})
            </button>
            <button
              onClick={() => setActiveTab('likes')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'likes'
                  ? 'border-primary-500 text-primary-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <Heart className="w-4 h-4 inline mr-2" />
              我的点赞
            </button>
            <button
              onClick={() => setActiveTab('following')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'following'
                  ? 'border-primary-500 text-primary-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <Users className="w-4 h-4 inline mr-2" />
              我的关注 ({profileUser.following_count})
            </button>
            <button
              onClick={() => setActiveTab('followers')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'followers'
                  ? 'border-primary-500 text-primary-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <Users className="w-4 h-4 inline mr-2" />
              我的粉丝 ({profileUser.followers_count})
            </button>
          </nav>
        </div>

        <div className="p-6">
          {renderTabContent()}
        </div>
      </div>
    </div>
  );
};

export default Profile;
