import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Heart, MessageCircle, Bookmark, MoreHorizontal, Clock, Users } from 'lucide-react';
import { Post } from '../../types/post';
import { postsApi } from '../../api/posts';
import { useQueryClient } from 'react-query';
import toast from 'react-hot-toast';

interface PostCardProps {
  post: Post;
  onLike?: (postId: number) => void;
  onBookmark?: (postId: number) => void;
}

const PostCard: React.FC<PostCardProps> = ({ post, onLike, onBookmark }) => {
  const queryClient = useQueryClient();
  const [isLiking, setIsLiking] = useState(false);
  const [isBookmarking, setIsBookmarking] = useState(false);
  const formatTime = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const hours = Math.floor(diff / (1000 * 60 * 60));

    if (hours < 1) {
      const minutes = Math.floor(diff / (1000 * 60));
      return `${minutes}分钟前`;
    } else if (hours < 24) {
      return `${hours}小时前`;
    } else {
      const days = Math.floor(hours / 24);
      return `${days}天前`;
    }
  };

  const handleLike = () => {
    onLike?.(post.id);
  };

  const handleBookmark = () => {
    onBookmark?.(post.id);
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200">
      {/* 帖子头部 */}
      <div className="flex items-center justify-between p-4 border-b border-gray-100">
        <div className="flex items-center space-x-3">
          <img
            src={post.author.avatar_url}
            alt={post.author.nickname}
            className="w-10 h-10 rounded-full"
          />
          <div>
            <div className="flex items-center space-x-2">
              <h3 className="font-medium text-gray-900">{post.author.nickname}</h3>
              {post.author.is_verified && (
                <span className="text-blue-500 text-xs">✓</span>
              )}
            </div>
            <div className="flex items-center text-sm text-gray-500">
              <Clock className="w-3 h-3 mr-1" />
              {formatTime(post.created_at)}
            </div>
          </div>
        </div>
        <button className="p-2 hover:bg-gray-100 rounded-full">
          <MoreHorizontal className="w-4 h-4 text-gray-400" />
        </button>
      </div>

      {/* 帖子内容 */}
      <div className="p-4">
        <Link to={`/post/${post.id}`} className="block">
          <h2 className="text-lg font-medium text-gray-900 mb-2 hover:text-primary-600">
            {post.title}
          </h2>
          <p className="text-gray-700 leading-relaxed mb-4 line-clamp-3">
            {post.content}
          </p>
        </Link>

        {/* 图片显示 */}
        {post.images && post.images.length > 0 && (
          <div className="mt-4">
            <div className="grid grid-cols-2 gap-2">
              {post.images.slice(0, 6).map((imageUrl, index) => (
                <img
                  key={index}
                  src={imageUrl}
                  alt={`图片 ${index + 1}`}
                  className="w-full h-32 object-cover rounded-lg"
                />
              ))}
            </div>
            {post.images.length > 6 && (
              <p className="text-sm text-gray-500 mt-2">
                还有 {post.images.length - 6} 张图片...
              </p>
            )}
          </div>
        )}

      </div>

      {/* 互动按钮 */}
      <div className="flex items-center justify-between px-4 py-3 border-t border-gray-100">
        <div className="flex items-center space-x-6">
          <button
            onClick={handleLike}
            className={`flex items-center space-x-1 transition-colors ${
              post.is_liked ? 'text-red-500' : 'text-gray-500 hover:text-red-500'
            }`}
          >
            <Heart className={`w-5 h-5 ${post.is_liked ? 'fill-current' : ''}`} />
            <span className="text-sm font-medium">{post.likes_count || 0}</span>
          </button>
          <Link
            to={`/post/${post.id}`}
            className="flex items-center space-x-1 text-gray-500 hover:text-primary-600 transition-colors"
          >
            <MessageCircle className="w-5 h-5" />
            <span className="text-sm font-medium">{post.comments_count || 0}</span>
          </Link>
        </div>
        <button
          onClick={handleBookmark}
          className={`p-2 rounded-full transition-colors ${
            post.is_bookmarked ? 'text-yellow-500' : 'text-gray-400 hover:text-yellow-500'
          }`}
        >
          <Bookmark className={`w-5 h-5 ${post.is_bookmarked ? 'fill-current' : ''}`} />
        </button>
      </div>
    </div>
  );
};

export default PostCard;
